import React from "react";
import "../css/style.css";
import { QaData } from '../data/QaData';
import arrow from "../image/qa/arrow.svg"
import useCollapse from 'react-collapsed';
import close from "../image/qa/close.svg"

export default function Qa() {
    const { getCollapseProps, getToggleProps, isExpanded } = useCollapse();
    return(
        <section className="qa">
            <div className="qa__content _container">
                <div className="qa__row">
                    <div className="qa__btn" {...getToggleProps()}>
                        {isExpanded ? 'Закрыть' : 'Открыть'}
                    </div>
                    <ul className="qa__list" >
                        {QaData.map((item, index) => {
                            return(
                                <li key={index} className={item.cName} {...getCollapseProps()}>
                                    <div className="qa__column">
                                        <img src={item.icon}/>
                                        {item.title}
                                    </div>
                                    <img src={arrow}/>
                                </li>
                            )
                        })}
                    </ul>
                </div>
            </div>
        </section>
    )
}
