import React from "react";

import "../../css/style.css";
import { ResultData } from "../../data/ResultData";
import edit from "../../image/table/edit.svg";
var i = 0;

export default class Table extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [],
            n: -1,
            kpgz: "",
            code: "",
            name: "",
            spgz: "",
            measure: "",
            unit: "",
            price: "",
            cost: "",
            address: "",
        }
        this.handleUpdateClick = this.handleUpdateClick.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.setState({n : this.state.n + 1})
        i+=1;
        if (i>-1 && ResultData[i-1] === undefined) {
            i-=1;
        }
    }

    handleUpdateClick() {
        this.forceUpdate();
            this.setState({
                items: ResultData[i],
            })
    }

    handleClick(id) {
        var items = [...this.state.items];
        items[id-1].kpgz = this.state.kpgz;
        items[id-1].code = this.state.code;
        items[id-1].name = this.state.name;
        items[id-1].spgz = this.state.spgz;
        items[id-1].measure = this.state.measure;
        items[id-1].unit = this.state.unit;
        items[id-1].price = this.state.price;
        items[id-1].cost = this.state.cost;
        items[id-1].address = this.state.address;
        this.setState({items});
        console.log(this.state.items);
    }

    handleChange = (event) => {
        switch (event.target.id) {
            case "kpgz":
                this.setState({kpgz: event.target.value});   
                break;
            case "code":
                this.setState({code: event.target.value});   
                break;
            case "name":
                this.setState({name: event.target.value});   
                break;
            case "spgz":
                this.setState({spgz: event.target.value});   
                break;
            case "measure":
                this.setState({measure: event.target.value});   
                break;
            case "unit":
                this.setState({unit: event.target.value});   
                break;
            case "price":
                this.setState({price: event.target.value});   
                break;
            case "cost":
                this.setState({cost: event.target.value});   
                break;
            case "address":
                this.setState({address: event.target.value});   
                break;
        }
    }

    render() {
        if (ResultData[i] === undefined) return (
            <div className="table-start__content _container">
                <div className="table-start " onClick={this.handleUpdateClick} id="update">Создать таблицу</div>
            </div>
        )
        
        return(
            <section className="table">
                <div className="table__content _container">
                    <div className="table__row">
                        <form className="table__form">
                            <input value={this.kpgz} onChange={this.handleChange} id="kpgz" placeholder="КПГЗ"/>
                            <input value={this.code} onChange={this.handleChange} id="code" placeholder="Шифр"/>
                            <input value={this.name} onChange={this.handleChange} id="name" placeholder="Наименование работ"/>
                            <input value={this.spgz} onChange={this.handleChange} id="spgz" placeholder="СПГЗ"/>
                            <input value={this.measure} onChange={this.handleChange} id="measure" placeholder="Единица измерения"/>
                            <input value={this.unit} onChange={this.handleChange} id="unit" placeholder="Количество"/>
                            <input value={this.price} onChange={this.handleChange} id="price" placeholder="Цена за ед. ТРУ, руб"/>
                            <input value={this.cost} onChange={this.handleChange} id="cost" placeholder="Стоимость за ТРУ, руб"/>
                            <input value={this.address} onChange={this.handleChange} id="address" placeholder="Адрес"/>
                        </form>
                        <ul className="table__list">
                            <li className="table__block">
                                <div className="table__hat number">№</div>
                                <div className="table__hat id">ID</div>
                                <div className="table__hat kpgz">КПГЗ</div>
                                <div className="table__hat code">Шифр</div>
                                <div className="table__hat name">Наименование работ</div>
                                <div className="table__hat cpgz">СПГЗ</div>
                                <div className="table__hat measure">Единица измерения</div>
                                <div className="table__hat unit">Количество</div>
                                <div className="table__hat price">Цена за ед. ТРУ, руб</div>
                                <div className="table__hat cost">Стоимость <br/> за ТРУ, руб</div>
                                <div className="table__hat address">Адрес</div>
                            </li>
                        </ul>
                        <ul className="table__list">
                            {this.state.items.map(( item, index ) => {
                                return(
                                    <li className="table__block" key={index}>
                                        <div className="table__box number"><img onClick={() => this.handleClick(item.num)} src={edit}/> {item.num}</div>
                                        <div className="table__box id">{item.id}</div>
                                        <div className="table__box kpgz">{item.kpgz === undefined ? "КПГЗ" : item.kpgz}</div>
                                        <div className="table__box code">{item.code}</div>
                                        <div className="table__box name" >{item.name.substring(0, 35).concat("...")}</div>
                                        <div className="table__box cpgz">{item.spgz === undefined ? "СПГЗ" : item.spgz}</div>
                                        <div className="table__box measure">{item.measure}</div>
                                        <div className="table__box unit">{item.unit}</div>
                                        <div className="table__box price">{item.price === undefined ? "Цена за ед. ТРУ, руб" : item.price}</div>
                                        <div className="table__box cost">{item.cost === undefined ? "Стоимость <br/> за ТРУ, руб" : item.cost}</div>
                                        <div className="table__box address">{item.address === undefined ? "Адрес" : item.address}</div>
                                    </li>
                                )
                            })}
                        </ul>
                    </div>
                </div>
            </section>
        )
    } 
}
