import React from "react";

import exp from "../../image/panel/export.svg";
import save from "../../image/panel/save.svg";

export default class BotPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            k: 0,
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(event) {
        this.setState({
            k: this.state.k + 1,
        })
        console.log(this.state.k);
    }

    saveStyle() {
        let style = {};
        if (this.state.k > 0) {
            style = {
                "background": "#FF5840",
                "cursor": "pointer",
            }
        }
        return style
    }

    render() {
        let style = {
            "background": "rgba(16, 16, 16, 0.2)",
            "cursor": "pointer",
        }
        if (this.state.k > 0) {
            style = {
                "background": "#FF5840",
                "cursor": "pointer",
            }
        }
        return(
            <section className="panel">
                <div className="panel__content _container">
                    <div className="panel__row">
                        <form className="panel__form">
                            <input
                            type="text"
                            placeholder="."
                            />
                        </form>
                        <div className="panel__btns" >
                            <div className="panel__export" onClick={this.handleClick}>
                                <img src={exp}/>
                                Экспорт
                            </div>
                            <div className="panel__save" style={style}>
                                <img src={save}/>
                                Сохранить
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}
