import React from "react";

import "../../css/style.css";
import { ResultData } from "../../data/ResultData";
var i = 0;

export default class Table extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [],
            n: -1,
            edit: [],
        }
        this.handleUpdateClick = this.handleUpdateClick.bind(this);
        this.handleClick = this.handleClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    componentDidMount() {
        this.setState({n : this.state.n + 1})
        i+=1;
        if (i>-1 && ResultData[i-1] === undefined) {
            i-=1;
        }
        console.log(i);
    }

    handleUpdateClick() {
        this.forceUpdate();
            console.log("update");
            this.setState({
                items: ResultData[i],
            })
    }

    handleClick(id, name) {
        var items = [...this.state.items];
        var edit = [...this.state.edit];
        items[id-1].num = edit.num;
        console.log(items[id-1].num);
        this.setState({items});
    }

    handleChange = (e) => {
        let next = this.state.edit;
        if (e.target.id === "num") {
            next.num = e.target.value;
        }
        this.setState({next})
    }
    
    render() {
        if (ResultData[i] === undefined) return (
            <div className="table-start__content _container">
                <div className="table-start " onClick={this.handleUpdateClick} id="update">Создать таблицу</div>
            </div>
        )
        
        console.log(this.state.items)
        return(
            <section className="table">
                <div className="table__content _container">
                    <div className="table__row">
                        <form className="table__form">
                            <label className="table__label">
                                №
                                <input value={this.state.edit.num} onChange={this.handleChange} id="num"/>
                            </label>
                        </form>
                        <ul className="table__list">
                            <li className="table__block">
                                <div className="table__hat number">№</div>
                                <div className="table__hat id">ID</div>
                                <div className="table__hat kpgz">КПГЗ</div>
                                <div className="table__hat code">Шифр</div>
                                <div className="table__hat name">Наименование работ</div>
                                <div className="table__hat cpgz">СПГЗ</div>
                                <div className="table__hat measure">Единица измерения</div>
                                <div className="table__hat unit">Количество</div>
                                <div className="table__hat price">Цена за ед. ТРУ, руб</div>
                                <div className="table__hat cost">Стоимость <br/> за ТРУ, руб</div>
                                <div className="table__hat address">Адрес</div>
                            </li>
                        </ul>
                        <ul className="table__list">
                            {this.state.items.map(( item, index ) => {
                                return(
                                    <li className="table__block" key={index}>
                                        <div className="table__box number">{item.num}</div>
                                        <div className="table__box id">{item.id}</div>
                                        <div className="table__box kpgz">КПГЗ</div>
                                        <div className="table__box code">{item.code}</div>
                                        <div className="table__box name" onClick={() => this.handleClick(item.num, item.name)}>{item.name.substring(0, 35).concat("...")}</div>
                                        <div className="table__box cpgz">СПГЗ</div>
                                        <div className="table__box measure">{item.measure}</div>
                                        <div className="table__box unit">{item.unit}</div>
                                        <div className="table__box price">Цена за ед. ТРУ, руб</div>
                                        <div className="table__box cost">Стоимость <br/> за ТРУ, руб</div>
                                        <div className="table__box address">Адрес</div>
                                    </li>
                                )
                            })}
                        </ul>
                    </div>
                </div>
            </section>
        )
    } 
}
