import React from "react";

import "../../css/style.css";
import { ResultData } from "../../data/ResultData";
import { n } from "../../routes/Create";
var i = 0;

export default class Table extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [],
            n: -1
        }
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount() {
        this.setState({n : this.state.n + 1})
        i+=1;
        if (i>-1 && ResultData[i-1] === undefined) {
            i-=1;
        }
        console.log(i);
    }

    handleClick() {
        this.forceUpdate();
        console.log("update")
    }
    
    render() {
        if (ResultData[i] === undefined) return <div onClick={this.handleClick}>123</div>
        
        return(
            <section className="table">
                <div className="table__content _container">
                    <div className="table__row">
                        <ul className="table__list">
                            {ResultData[i].map(( item, index ) => {
                                return(
                                    <li className="table__block" key={index}>
                                        <textarea
                                        label="Фамилия Имя Отчество"
                                        type="text"
                                        value={item.number}>{item.number}</textarea>
                                    </li>
                                )
                            })}
                        </ul>
                    </div>
                </div>
            </section>
        )
    } 
}
