import React from "react";

import exp from "../../image/panel/export.svg";
import save from "../../image/panel/save.svg";

export default class BotPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            k: 0,
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(event) {
        this.setState({
            k: this.k+1,
        })
        console.log(this.k);
    }

    render() {
        return(
            <section className="panel">
                <div className="panel__content _container">
                    <div className="panel__row">
                        <form className="panel__form">
                            <input
                            type="text"
                            placeholder="."
                            />
                        </form>
                        <div className="panel__btns" onClick={this.handleClick}>
                            <div className="panel__export">
                                <img src={exp}/>
                                Экспорт
                            </div>
                            <div className="panel__save">
                                <img src={save}/>
                                Сохранить
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}
